'use strict';

// Load modules


// Declare internals

var internals = {};


exports.drain = function (stream, callback) {

    var read = () => stream.read();
    var end = () => {

        stream.removeListener('readable', read);
        stream.removeListener('error', end);
        stream.removeListener('end', end);

        if (callback) {
            return callback();
        }
    };

    stream.on('readable', read);
    stream.once('error', end);
    stream.once('end', end);
};
